  user_id, notice_user_id, relation_type, 
  relation_id, action_type, have_read, 
  desc, image, parent_id
  )
values (#{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=BIT}, 
  #{userId,jdbcType=INTEGER}, #{noticeUserId,jdbcType=INTEGER}, #{relationType,jdbcType=VARCHAR}, 
  #{relationId,jdbcType=INTEGER}, #{actionType,jdbcType=VARCHAR}, #{haveRead,jdbcType=BIT}, 
  #{desc,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}, #{parentId,jdbcType=INTEGER}
  )
